import React from 'react';
import './App.css';
import { BrowserRouter, Routes, Route, } from 'react-router-dom';
import  { Login } from './Login/Login.js';
import {StudentsTable} from './StudentsTable/StudentsTable.js'
import {defaultStudentsTable} from './StudentsTable/StudentsTable.js'



function App() {

  const [studentsList, setStudentsList] = React.useState(defaultStudentsTable);
  const [searchValue, setSearchValue] = React.useState('');
  let searchedStudents = []

  if (!searchValue.length >= 1){
    searchedStudents = studentsList
  }else{
    searchedStudents = studentsList.filter (student => {
      const studentText = student.text.toLowerCase();
      const searchText = searchValue.toLocaleLowerCase;
      return studentText.includes(searchText)
    })
  }

  return (
    <div className="wrapper">
      <BrowserRouter>
        <Routes>
          <Route path="/Login" element={
          <Login
            searchValue={searchValue}
            setSearchValue={setSearchValue}

          />}/>
          <Route path="/StudentsTable" element={<StudentsTable/>}/>   
        </Routes>
      </BrowserRouter>
    </div>  
  );
}

export default App;
