    return(

        <React.Fragment>

            <FilterComponent
                searchValue={searchValue}
                onSearchValueChange={(e) => {setSearchValue(e.target.value);}}
            />


            <DataTable
                columns = {columns}
                key= {filteredStudents.email}
                data = {filteredStudents}
                noHeader
                pagination
