        sortable: true,
    },
    {
        name: "País",
        selector: row => row.country,
        sortable: true,
    },
    {
        name: "Teléfono",
        selector: row =>row.phone,
        sortable: true,
    },
    {
        name: "Email",
        selector: row=>row.email,
        sortable: true,

    },
    {
        name: "Etiquetas",
        selector: row=>row.tags,
        sortable: true,
        cell: d => d.tags.map(tag =>(<span className="etiqueta">{tag}</span>))
    }
]

